pair E=(24,72), F=(24,24), G=(72,24), H=(72,72);
pen d = linetype("8 8");

draw(A--D--C--B--cycle);
draw(D--H--G--C);
draw(A--E--H);

draw(B--F--E);
draw(F--G);
draw(H--A--G, d);
label("$x$", (40, 61), N);
label("$y$", (40, 35), N);
[/asy]

