[asy]

size(200);
pair p1,p2,p3,p4;
p1 = (0,0); p2 = (2.5, 4); p3 = (7.5,4); p4 = (10,0);

draw(p1--p2--p3--p4--cycle);
draw(p1--p3); draw(p2--p4);
label("$A$", p1, SW);
label("$D$", p2, NW);
label("$C$", p3, NE);
label("$B$", p4, SE);
label("$E$", (5,2.5) , S);

[/asy]

