def base_n_to_decimal(number, base):
    """Convert a number from base-n to decimal."""
    decimal = 0
    for i, digit in enumerate(reversed(number)):
        if digit.isdigit():
            value = int(digit)
        else:
            value = ord(digit) - ord('a') + 10
        decimal += value * (base ** i)
    return decimal

def decimal_to_base_n(decimal, base):
    """Convert a decimal number to base-n."""
    if decimal == 0:
        return "0"
    digits = []
    while decimal:
        remainder = decimal % base
        if remainder < 10:
            digits.append(str(remainder))
        else:
            digits.append(chr(remainder - 10 + ord('a')))
        decimal //= base
    return ''.join(reversed(digits))

