def base_n_to_decimal(number, base):
    """Convert a number from a given base to decimal."""
    decimal = 0
    for index, digit in enumerate(reversed(number)):
        if '0' <= digit <= '9':
            value = int(digit)
        else:
            value = ord(digit) - ord('a') + 10
        decimal += value * (base ** index)
    return decimal

def decimal_to_base_n(decimal, base):
    """Convert a decimal number to a given base."""
    if decimal == 0:
        return "0"
    digits = []
    while decimal:
        remainder = decimal % base
        if remainder < 10:
            digits.append(str(remainder))
        else:
            digits.append(chr(remainder - 10 + ord('a')))
        decimal //= base
    return ''.join(reversed(digits))

